<!DOCTYPE html>
<html lang="en">
<head>
  <meta charset="UTF-8">
  <title>Vue Image Conversion</title>
  <script src="https://cdn.jsdelivr.net/npm/vue@2/dist/vue.js"></script>
</head>
<body>
<div id="app">
  <image-converter></image-converter>
</div>

<script>
  // Vue组件定义
  const ImageConverter = {
    template: `
        <div>
          <img :src="convertedImageUrl" alt="Converted Image" />
          <textarea v-model="base64String" placeholder="data:image/png;base64,"></textarea>
          <button @click="convertBase64ToImage">Convert</button>
        </div>
      `,
    data() {
      return {
        base64String: '',
        convertedImageUrl: ''
      };
    },
    methods: {
      convertBase64ToImage() {
        if (this.base64String.startsWith('data:image/')) {
          this.convertedImageUrl = this.base64String;
        } else {
          this.convertedImageUrl = `data:image/jpeg;base64,${this.base64String}`;
        }
      }
    },
    watch: {
      base64String(newVal) {
        this.convertBase64ToImage();
      }
    }
  };

  // 创建Vue实例并挂载组件
  new Vue({
    el: '#app',
    components: {
      'image-converter': ImageConverter
    }
  });
</script>
</body>
</html>
<script setup lang="ts">
</script>